      try {
        // åˆå§‹åŒ–åœ°å›¾
        mapInstance.current = new window.AMap.Map(mapRef.current, {
          zoom: 10,
          center: [116.397428, 39.90923], // åŒ—äº¬ä¸­å¿ƒç‚¹
