    // æ¸…ç†å‡½æ•°
    return () => {
      clearTimeout(timer);
      if (mapInstance.current) {
        try {
          mapInstance.current.destroy();
        } catch (error) {
          console.warn('åœ°å›¾é”€æ¯æ—¶å‡ºé”™:', error);
        }
      }
