    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, []);

  const showFallbackMap = () => {
    // æ˜¾ç¤ºæ¨¡æ‹Ÿåœ°å›¾ç•Œé¢
    if (mapRef.current) {
      mapRef.current.innerHTML = `
        <div style="
          width: 100%;
          height: 100%;
          background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
          position: relative;
          display: flex;
