    // åˆ›å»ºæ ‡è®°ç‚¹
    const marker = new window.AMap.Marker({
      position: new window.AMap.LngLat(item.longitude, item.latitude),
      icon: icon,
      title: item.name,
      extData: item
    });

    // æ·»åŠ ç‚¹å‡»äº‹ä»¶
    marker.on('click', (e) => {
      showInfoWindow(e.target);
    });

    // å°†æ ‡è®°æ·»åŠ åˆ°åœ°å›¾
    mapInstance.current.add(marker);
