    infoWindow.open(mapInstance.current, new window.AMap.LngLat(data.location.lng, data.location.lat));
  };

  return (
    <div className="map-container">
      <div className="map-controls">
        <div className="legend">
          <h4>å›¾ä¾‹</h4>
          <div className="legend-item">
